import '../../styles/admin.css';
import { Button } from 'react-bootstrap';
import React, { useState, useEffect} from "react";
import { checkDatabaseConnection } from "../http/checkBaseConnectionAPI";

const RosAvtoDor = () => {
  const [databaseStatus, setDatabaseStatus] = useState("");

  useEffect(() => {
    const checkConnectionStatus = async () => {
      try {
        const isConnected = await checkDatabaseConnection();
        setDatabaseStatus(isConnected ? "В сети" : "Нет подключения");
      } catch (error) {
        console.error("Ошибка при проверке подключения к базе данных:", error);
        setDatabaseStatus("Ошибка");
      }
    };
    checkConnectionStatus();
    
    return () => {
      setDatabaseStatus("");
    };
  }, []);
    return (
      <div className="card">
      <div className="card-header">
        <h3>Информация об реестрах "РосАвтоДор"</h3>
      </div>
      <div className="card-body">
        <div className="card-field">
          <label>Статус:</label>
          <label>{databaseStatus}</label>
        </div>
        <div className="card-field">
          <label>Дата обновления:</label>
          <label>Дата обновления:</label>
        </div>
        <div className="card-field">
          <label>Ответственный:</label>
          <label>Дата обновления:</label>
        </div>
        <div className="card-field">
          <label>Телефон:</label>
          <label>Телефон:</label>
        </div>
        <div className="card-field">
          <label>Почта:</label>
          <label>Почта:</label>
        </div>
      </div>
      <div className="card-footer">
        <Button variant="primary">Проверить наличие обновлений</Button>
        <Button variant="primary">Обновить данные БД</Button>
        <Button variant="primary">Обновить структуру БД</Button>
      </div>
    </div>
  );
};
  export default RosAvtoDor;
