import React, { useState } from "react";
import companyAPI from "../http/companyAPI";
import CompanyStore from "../store/CompanyStore";

const SearchForm = ({ CompanyStore }) => {
  const [taxId, setTaxId] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const result = await companyAPI.searchCompanies(taxId);
      CompanyStore.setCompanies(result); // Сохраняем результаты поиска в хранилище
      setSearchResults(result); // Обновляем результаты поиска в компоненте

      // Очищаем поля ввода после выполнения поиска
      setTaxId("");
    } catch (error) {
      // Обрабатываем ошибку, если поиск не удался
      console.log("Ошибка при поиске компаний:", error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Tax ID"
          value={taxId}
          onChange={(e) => setTaxId(e.target.value)}
        />{" "}
        <button type="submit"> Поиск </button>{" "}
      </form>{" "}
      {/* Отображаем результаты поиска */}{" "}
      <div>
        {" "}
        {searchResults.map((company) => (
          <div key={company.id}>
            <h3> {company.name} </h3>{" "}
            {/* Выводите дополнительную информацию о компании по вашему усмотрению */}{" "}
          </div>
        ))}{" "}
      </div>{" "}
    </div>
  );
};

const App = () => {
  const companyStore = new CompanyStore();

  return (
    <div>
      <SearchForm companyStore={companyStore} />{" "}
    </div>
  );
};

export default App;
