// Файл CatObj.js
import React, { useState } from "react";
import companyAPI from "../http/companyAPI";
import CompanyStore from "../store/CompanyStore";

const SearchForm = ({ CompanyStore }) => {
    const [tax_id, settax_id] = useState("");
    const [searchResults, setSearchResults] = useState([]);

    const handleSubmit = async(e) => {
        e.preventDefault();

        try {
            const result = await companyAPI.searchCompanies(tax_id);
            CompanyStore.setCompanies(result);
            setSearchResults(result);
            settax_id("");
            console.log(result);
        } catch (error) {
            console.log("Ошибка при поиске компаний:", error);
        }
    };

    return ( <
        div >
        <
        form onSubmit = { handleSubmit } >
        <
        input type = "text"
        placeholder = "Tax ID"
        id = "tax_id"
        value = { tax_id }
        onChange = {
            (e) => settax_id(e.target.value) }
        />{" "} <
        button type = "submit" > Поиск < /button>{" "} <
        /form>{" "} { /* Отображаем результаты поиска */ } { " " } <
        div > { " " } {
            searchResults.map((companies) => ( <
                div key = { companies.companies_id } >
                <
                h3 > { companies.name } < /h3>{" "}

                { /* Выводите дополнительную информацию о компании по вашему усмотрению */ } { " " } <
                /div>
            ))
        } { " " } <
        /div>{" "} <
        /div>
    );
};

const App = () => {
    const companyStore = new CompanyStore();

    return ( <
        div >
        <
        SearchForm CompanyStore = { companyStore }
        />{" "} <
        /div>
    );
};

export default App;
