// Файл CatObj.js
import React, { useState } from "react";
import companyAPI from "../http/companyAPI";
import CompanyStore from "../store/CompanyStore";
import CompanyCard from "../components/CompanyCard";

const SearchForm = ({ companyStore }) => {
  const [tax_id, settax_id] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const result = await companyAPI.searchCompanies(tax_id);
      companyStore.setCompanies(result);
      setSearchResults(result);
      setname("");
      setgovernment_registration_number("");
      settax_id("");
      console.log(result);
    } catch (error) {
      console.log("Ошибка при поиске компаний:", error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Название компании"
          id="name"
          value={tax_id}
          onChange={(e) => setname(e.target.value)}
        />
         <input
          type="text"
          placeholder="ОГРН"
          id="government_registration_number"
          value={government_registration_number}
          onChange={(e) => setgovernment_registration_number(e.target.value)}
        />
         <input
          type="text"
          placeholder="ИНН Компании"
          id="tax_id"
          value={tax_id}
          onChange={(e) => settax_id(e.target.value)}
        />
        <button type="submit"> Поиск </button>{" "}
      </form>{" "}
      {/* Отображаем результаты поиска */}{" "}
      <div>
        {searchResults.map((company) => (
          <CompanyCard key={company.companies_id} company={company} />
        ))}
      </div>{" "}
    </div>
  );
};

const App = () => {
  const companyStore = new CompanyStore();

  return (
    <div>
      <SearchForm companyStore={companyStore} />{" "}
    </div>
  );
};

export default App;
