import React from "react";
import Sidebar from "../components/Sidebar";
import MainContent from "../components/MainContent";
import { Button } from "react-bootstrap";
import { runParser } from "../http/parserAPI";
import Control from './pages/Admin/Control'; // Импортируйте компонент Control из соответствующего пути

const Admin = () => {
  const [selectedLink, setSelectedLink] = React.useState(null);
  const [selectedContent, setSelectedContent] = React.useState(null);

  const handleLinkClick = (link, content) => {
    setSelectedLink(link);
    setSelectedContent(content);
  };
  const handleRunParser = async () => {
    try {
      const result = await runParser();
      console.log("Парсер успешно запущен", result);
      // Дополнительная обработка результата
    } catch (error) {
      console.error("Ошибка при запуске парсера", error);
      // Обработка ошибки
    }
  };

  return (
    <div>
      <Sidebar handleLinkClick={handleLinkClick} />
      <div>
        {selectedLink === "./pages/Admin/Control" && selectedContent === "Содержимое 1" && (
          <Control />
        )}
        {/* Другие условия для отображения содержимого других страниц */}
      </div>
      {/* <Button onClick={handleRunParser}>Запустить парсер</Button> */}
    </div>
  );
};

export default Admin;
