import React, { useState } from "react";
import Button from 'react-bootstrap/Button';
import { Card } from "react-bootstrap";
import ObjectCard from "../components/ObjectCard";

const CompanyCard = ({ company }) => {
  const [identifiers, setIdentifiers] = useState([]);
  const [showObjectCards, setShowObjectCards] = useState(false); // Состояние для отображения компонентов ObjectCard

  const handleShowObjectCards = () => {
    setShowObjectCards(true); // Устанавливаем состояние для отображения компонентов ObjectCard
  };
  // Сохранение идентификаторов при монтировании компонента
  React.useEffect(() => {
    const parsedIdentifiers = JSON.parse(company.identifiers);
    setIdentifiers(parsedIdentifiers);
  }, [company.identifiers]);

  return (
    <Card border="primary" style={{ margin: 10}}>
      <Card.Body>
      <Card.Header>{company.name}</Card.Header>
        <Card.Text>
          <strong>ID:</strong> {company.company_id} <br />
          <strong>ИП или ЮЛ:</strong> {company.type} <br />
          <strong>Объект:</strong> {company.subject} <br />
          <strong>Адрес:</strong> {company.address} <br />
          <strong>ИНН:</strong> {company.index} <br />
          <strong>ОГРН:</strong> {company.ogrn} <br />
          <strong>Дата создания организации:</strong> {company.create_org_date} <br />
        </Card.Text>   
        <Button variant="primary" onClick={handleShowObjectCards} style={{ margin: 10, width: "20em"}}>Посмотреть объекты организации</Button>
      </Card.Body>
      {showObjectCards && (
        <div>
          {company.identifiers.split(":").map((identifier) => (
            <ObjectCard key={identifier} identifier={identifier} />
          ))}
        </div>
      )}
    </Card>
  );
};

export default CompanyCard;
